import React from 'react';
import { ImageBackground, StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

const image = require('./assets/Images/Stade.jpeg');

const HomeScreen = ({ navigation }) => (
  <View style={styles.container}>
    <ImageBackground source={image} resizeMode="cover" style={styles.image}>
      <Text style={styles.text}>Bienvenue au BenGym</Text>
      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Séances')}>
          <Text style={styles.buttonText}>Les séances</Text>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  </View>
);

const SeancesScreen = () => (
  <View style={styles.container}>
    <Text style={styles.text}>Page des séances</Text>
    {/* Ajoutez le contenu de votre page des séances ici */}
  </View>
);

const Stack = createStackNavigator();

const App = () => (
  <NavigationContainer>
    <Stack.Navigator>
      <Stack.Screen name="Home" component={HomeScreen} />
      <Stack.Screen name="Seances" component={SeancesScreen} />
    </Stack.Navigator>
  </NavigationContainer>
);

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  image: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },

  text: {
    color: 'white',
    fontSize: 40,
    lineHeight: 50,
    fontWeight: 'bold',
    textAlign: 'left',
    padding: 40,
  },

  buttonContainer: {
    alignItems: 'center',
    marginTop: 20,
  },

  button: {
    alignItems: 'center',
    justifyContent: 'center',
    width: 200,
    height: 40,
    backgroundColor: 'black',
  },

  buttonText: {
    color: 'white',
  },
});

export default App;
