import React from 'react';
import { ImageBackground, StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

const image = require('./assets/Images/Stade.jpeg');

/* HomePage */
const HomeScreen = ({ navigation }) => (
  <View style={styles.container}>
    <ImageBackground source={image} resizeMode="cover" style={styles.image}>
      <Text style={styles.text}>Bienvenue au BenGym</Text>
        <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Séances')}>
            <Text style={styles.buttonText}>Saison 3</Text>
          </TouchableOpacity>
        </View>
    </ImageBackground>
  </View>
);

/* Vue sur toutes les séances */
const SeancesScreen = () => (
  <View style={styles.container}>
    <Text style={styles.title_saison}>Saison 3</Text>

    <View style={styles.all_buttons}>
      <TouchableOpacity style={styles.button_seance}>
        <Text style={styles.buttonText}>Séance 1 (Pecs / Dos) </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button_seance}>
        <Text style={styles.buttonText}>Séance 2 (Jambes / Cardio) </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button_seance}>
        <Text style={styles.buttonText}>Séance 3 (Bras) </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button_seance}>
        <Text style={styles.buttonText}>Séance 4 (Circuit training) </Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button_seance}>
        <Text style={styles.buttonText}>Séance 5 (Cardio) </Text>
      </TouchableOpacity>
    </View>
  </View>
);

const Stack = createStackNavigator();

const App = () => (
  <NavigationContainer>
    <Stack.Navigator>
      <Stack.Screen name="Home" component={HomeScreen} />
      <Stack.Screen name="Séances" component={SeancesScreen} />
    </Stack.Navigator>
  </NavigationContainer>
);

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  image: {
    flex: 1,
  },

  text: {
    color: 'white',
    fontSize: 40,
    lineHeight: 50,
    fontWeight: 'bold',
    textAlign: 'left',
    padding: 40,
  },

  buttonContainer: {
    alignItems: 'center',
    marginTop: 20,
  },

  button: {
    alignItems: 'center',
    justifyContent: 'center',
    width: 200,
    height: 40,
    backgroundColor: 'black',
  },

  buttonText: {
    color: 'white',
  },

  title_saison: {
    color: 'black',
    fontSize: 40,
    lineHeight: 50,
    fontWeight: 'bold',
    textAlign: 'left',
    padding: 40,
  },

  all_buttons: {
    padding: 20,
  },

  button_seance: {
    justifyContent: 'center',
    padding: 20,
    width: 250,
    height: 70,
    backgroundColor: 'grey',
    marginBottom: 30,
  },
});

export default App;
